import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        String str1=sc.next().toLowerCase();        String str2=sc.next().toLowerCase();        if(str1.equals(str2)){            System.out.println(0);        }        else{            for(int i=0;i<str1.length();i++){                if(str1.charAt(i)<str2.charAt(i)){                    System.out.println(-1);                    break;                }                else if(str1.charAt(i)>str2.charAt(i)){                    System.out.println(1);                    break;                }            }        }    }}