import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0){            int n=sc.nextInt();            LinkedHashSet<Integer> hs =new LinkedHashSet<>();            for(int i=0;i<2*n;i++){                hs.add(sc.nextInt());            }            for(int nums: hs) System.out.print(nums+" ");            System.out.println();        }    }}