import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0){            int c1=0,c2=0;            int n=sc.nextInt();            int[] arr=new int[n];            for(int i=0;i<n;i++){                arr[i]=sc.nextInt();                if(arr[i]==1) c1++;                else c2++;            }            int sum=c1+2*c2;            if(sum%2!=0){                System.out.println("NO");            }            else{                int half=sum/2;                if(half%2==1&&c1==0){                    System.out.println("NO");                }                else{                    System.out.println("YES");                }            }        }    }}