import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0) {            int a=sc.nextInt();            int x=sc.nextInt();            int y=sc.nextInt();            if ((x<a&&a<y)||(y<a&&a<x))                System.out.println("NO");            else                System.out.println("YES");        }    }}