import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0){            int avg=Integer.MIN_VALUE;            int max=Integer.MIN_VALUE;            int n=sc.nextInt();            int[] arr=new int[n];            for(int i=0;i<n;i++){                arr[i]=sc.nextInt();                if(arr[i]>max){                    max=arr[i];                }            }            int i=0,j=0;            for(i=0;i<n-1;i++){                int sum=0;                for(j=i+1;j<n;j++){                    sum+=arr[i];                }                    avg=Math.max(avg,(1/(j-i)*sum));            }            if(avg<max){                System.out.println(max);            }            else{                System.out.println(avg);            }        }    }}