import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        int x=0;        while(t-->0){            String str=sc.next();            if(str.charAt(0)=='+'){                ++x;            }            else if(str.charAt(1)=='+'){                x++;            }            else if(str.charAt(0)=='-'){                --x;            }            else{                x--;            }        }        System.out.println(x);    }}