import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        String str=sc.next();        int oc=0,tc=0,thc=0,p=0;        for(int i=0;i<str.length();i++){            if(str.charAt(i)=='1'){                oc++;            }            else if(str.charAt(i)=='2'){                tc++;            }            else if(str.charAt(i)=='3'){                thc++;            }            else{                p++;            }        }        StringBuilder sb=new StringBuilder();        int k=0;        while(oc>0){            sb.append(1);            if(k<p)                sb.append('+');            oc--;            k++;        }        k=0;        while(tc>0){            sb.append(2);            if(k<p)                sb.append('+');            tc--;            k++;        }        k=0;        while(thc>0){            sb.append(3);            if(k<p)                sb.append('+');            thc--;            k++;        }        if(sb.charAt(sb.length()-1)=='+')            sb.deleteCharAt(sb.length()-1);        str=sb.toString();        System.out.println(str);    }}