import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner s = new Scanner(System.in);        int n=s.nextInt();        String a=s.next();        int c=1;        for (int i=1;i<n;i++) {            String b=s.next();            if (!b.equals(a))                c++;            a = b;        }        System.out.print(c);    }}