import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        String str=sc.next();        int lc=0,uc=0;        for(int i=0;i<str.length();i++){            if(str.charAt(i)>96&&str.charAt(i)<123){                lc++;            }            else{                uc++;            }        }        if(lc>=uc) str=str.toLowerCase();        else str=str.toUpperCase();        System.out.println(str);    }}