import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        String s=sc.next();        int up=0,lw=0;        char[] ch=s.toCharArray();        for(int i=0;i<s.length();i++){            if(Character.isUpperCase(ch[i])) up++;            else lw++;        }        if(up>lw){            for(int i=0;i<s.length();i++){                ch[i]=Character.toUpperCase(ch[i]);            }        }        else{             for(int i=0;i<s.length();i++){                ch[i]=Character.toLowerCase(ch[i]);            }        }        for(int i=0;i<s.length();i++) System.out.print(ch[i]);    }}